sys_script_client.script:
function onLoad() {
	//New record
	if(g_form.isNewRecord()){
		//console.log("#### NeedIt Set Requested for - Logged in user ID:" + g_user.userID);
		g_form.setValue('u_requested_for', g_user.userID);
	}
	else{
		var requestor = g_form.getReference('u_requested_for', getCaller);
	}

}

function getCaller(requestor){

	//reference is passed into callback as first arguments
	var requestorName = requestor.first_name;

	jslog("NeedIt Set Requested for #### This message is from jslog().");
	jslog("NeedIt Set Requested for #### The value of Requested for is " + requestorName + " - which ID is : " + g_form.getValue('u_requested_for'));
	jslog("NeedIt Set Requested for #### The NeedIt State is " + g_form.getValue('state'));
	jslog("NeedIt Set Requested for #### The currently logged in user is " + g_user.getFullName() + ".");

}
